
# сортировка перестановками
def perestan(list):
    count = 0
    for i in range(1, len(list)):
        per = list[i]
        j = i - 1
        while (j >= 0 and list[j] < per): # сортировка по возрастанию или убыванию
            list[j + 1] = list[j]
            j -= 1
            count += 1
        list[j + 1] = per
    return f'{list}, количество итераций - {count}'

